<?php include "autoload.php";include "config.db.php";include "functions.php";$toplam = count($_FILES["dosya"]["name"]);$formatlar = array("image/png","image/jpeg");$table = $_GET["table"];for ($i = 0; $i < $toplam; $i++){    if (in_array($_FILES["dosya"]["type"][$i], $formatlar)){        $isim = rand(999,99999999999);        $uzanti = substr($_FILES["dosya"]["name"][$i],-4,4);        $dizin = "../../uploads/$table/".$isim.$uzanti;        $small = "../../uploads/$table/small-".$isim.$uzanti;        $medium = "../../uploads/$table/medium-".$isim.$uzanti;        if (move_uploaded_file($_FILES["dosya"]["tmp_name"][$i], $dizin)){            $resizeObj = new resize($dizin);        if($table=="banners") {            $resizeObj -> resizeImage(1920,506,'auto');            $resizeObj -> saveImage($dizin);        }            else {                $resizeObj -> resizeImage(800,600,'auto');                $resizeObj -> saveImage($dizin);            }            $resizeObj -> resizeImage(450,300,'crop');            $resizeObj -> saveImage($medium);            $resizeObj -> resizeImage(220,220, 'crop');            $resizeObj -> saveImage($small);            echo '<img style="width:50px" src="../uploads/'.$table.'/small-'.$isim.$uzanti.'" alt="" class="resim" />				<input type="hidden" name="data[file]" value="'.$isim.$uzanti.'" />';        }else {            echo '<span style="color:red">Dosya Yuklenemedi!!</span></form>';        }    }else {        echo '<span style="color:red">Yanlis Format!!</span></form>';    }}